SELECT 
FROM market_product
WHERE platform = #{platform}
  AND platform_product_id = #{platformProductId}

SELECT 
FROM market_product


        AND platform = #{platform}


        AND (title LIKE CONCAT('%', #{keyword}, '%')
             OR platform_product_id LIKE CONCAT('%', #{keyword}, '%'))


        AND brand = #{brand}


        AND category_id = #{categoryId}


ORDER BY last_seen_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM market_product


        AND platform = #{platform}


        AND (title LIKE CONCAT('%', #{keyword}, '%')
             OR platform_product_id LIKE CONCAT('%', #{keyword}, '%'))


        AND brand = #{brand}


        AND category_id = #{categoryId}

SELECT DISTINCT brand FROM market_product WHERE brand IS NOT NULL AND brand != ''
AND platform = #{platform}
ORDER BY brand

SELECT DISTINCT category_path FROM market_product WHERE category_path IS NOT NULL AND category_path != ''
AND platform = #{platform}
ORDER BY category_path
