mapper.select:
SELECT * FROM ozon_product_status


        AND id = #{id}


        AND product_id = #{productId}


        AND status = #{status}


        AND moderate_status = #{moderateStatus}


        AND validation_status = #{validationStatus}


ORDER BY id

mapper.insert:
INSERT INTO ozon_product_status (
    product_id, is_created, moderate_status, status, status_description,
    status_failed, status_name, status_tooltip, status_updated_at,
    validation_status, created_at, updated_at
) VALUES (
    #{productId}, #{isCreated}, #{moderateStatus}, #{status}, #{statusDescription},
    #{statusFailed}, #{statusName}, #{statusTooltip}, #{statusUpdatedAt},
    #{validationStatus}, #{createdAt}, #{updatedAt}
)
