mapper.select:
SELECT * FROM ozon_product_stock


        AND id = #{id}


        AND product_id = #{productId}


        AND sku = #{sku}


        AND source = #{source}


        AND present = #{present}


        AND reserved = #{reserved}


ORDER BY product_id, source, id

mapper.insert:
INSERT INTO ozon_product_stock (
    product_id, sku, source, present, reserved, created_at, updated_at
) VALUES (
    #{productId}, #{sku}, #{source}, #{present}, #{reserved}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE ozon_product_stock SET
    sku = #{sku},
    present = #{present},
    reserved = #{reserved},
    updated_at = #{updatedAt}
WHERE product_id = #{productId} AND source = #{source}
